import swap from "./swap";

function Merge(array, temp, left, mid, right, animation) {
  var k = left;
  var i = left;
  var j = mid + 1;

  while (i <= mid && j <= right) {
    animation.push([i, k]);
    animation.push([i, k]);
    console.log("push i k");
    console.log(i, j, k);
    if (temp[i] >= temp[j]) {
      animation.push([k, temp[i]]);
      animation.push([k, temp[i]]);
      //temp[k++] = array[i++];
      array[k++] = temp[i++];
      //   swap(array, k++, i++);

      console.log(i, k);
      //   i++;
      //   k++;
    } else {
      animation.push([k, temp[j]]);
      animation.push([k, temp[j]]);
      //   temp[k++] = array[j++];
      array[k++] = temp[j++];
      //   swap(array, k++, j++);
      console.log(j, k);
      //   j++;
      //   k++;
    }
  }
  while (i <= mid) {
    console.log("push i i");
    console.log(i);
    animation.push([i, i]);
    animation.push([i, i]);
    animation.push([k, temp[i]]);
    animation.push([k, temp[i]]);
    //temp[k++] = array[i++];
    array[k++] = temp[i++];
  }
  while (j <= right) {
    console.log("push j j");
    console.log(j);
    animation.push([j, j]);
    animation.push([j, j]);
    animation.push([k, temp[j]]);
    animation.push([k, temp[j]]);
    //temp[k++] = array[j++];
    array[k++] = temp[j++];
  }
}

function mergeSortHelper(array, temp, left, right, animation) {
  if (left === right) return;
  const mid = Math.floor((left + right) / 2);
  mergeSortHelper(array, temp, left, mid, animation);
  mergeSortHelper(array, temp, mid + 1, right, animation);
  Merge(array, temp, left, mid, right, animation);
}

function MergeSort(array) {
  var animation = [];
  if (array.length <= 1) return array;
  const temp = array.slice();
  mergeSortHelper(array, temp, 0, array.length - 1, animation);
  return animation;
}

export default MergeSort;
