import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";

const mapPropsToBGC = (isCompared, isSwapped) => {
  if (isCompared) {
    return "var(--compared)";
  }
  if (isSwapped) {
    return "var(--swapped)";
  }
  return "var(--copyOtherSidebar)";
};

const mapPropsToTransition = (speed) => {
  const minInterval = Math.min(300, speed);
  return `all ease-in-out ${minInterval}ms`;
};

export default function SortingBar(props) {
  return (
    <div
      id="sortBar"
      className="Sort_Bar"
      style={{
        position: "absolute",
        display: "flex",
        flex: "1",
        bottom: "0",
        width: `${props.width}px`,
        height: `${props.height}px`,
        left: `${props.index * (props.width + 2)}px`,
        backgroundColor: `${(props) =>
          mapPropsToBGC(props.isCompared, props.isSwapped)}`,
        transition: `${(props) => mapPropsToTransition(props.speed)}`,
      }}
    ></div>
  );
}
