import React from "react";
import { connect } from "react-redux";
import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import Tooltip from "@material-ui/core/Tooltip";
import Slider from "@material-ui/core/Slider";
import Grid from "@material-ui/core/Grid";
import FormControl from "@material-ui/core/FormControl";
import MenuItem from "@material-ui/core/MenuItem";
import Select from "@material-ui/core/Select";
import InputLabel from "@material-ui/core/InputLabel";
import Button from "@material-ui/core/Button";
import ThemeSwitch from "./ThemeSwitch";
import {
  setArrayLen,
  setShuffleAlgorithm,
  setSortAlgorithm,
  startShuffle,
  startSorting,
} from "../actions";
const useStyles = makeStyles({
  control: {
    width: "80%",
    padding: "20px",
    textAlign: "left",
    margin: "20px",
  },
  text: {},
  barDiv: {
    display: "flex",
    width: "60%",
  },
  button: {
    margin: "10px",
  },
  formControl: {
    width: "80%",
  },
  root: { marginTop: "30px" },
});
function ValueLabelComponent(props) {
  const { children, open, value } = props;

  const popperRef = React.useRef(null);
  React.useEffect(() => {
    if (popperRef.current) {
      popperRef.current.update();
    }
  });

  return (
    <Tooltip
      PopperProps={{
        popperRef,
      }}
      open={open}
      enterTouchDelay={0}
      placement="top"
      title={value}
    >
      {children}
    </Tooltip>
  );
}

const Control = ({
  sortingTarget,
  isSortReady,
  arrayLength,
  setArrayLen,
  sortAlgo,
  shuffleAlgo,
  startSorting,
  startShuffle,
}) => {
  const classes = useStyles();
  const handelLenChange = (e, value) => {
    setArrayLen(value);
  };
  const handelStartSorting = (e) => {
    startSorting(!isSortReady);
  };
  return (
    <>
      <Grid container spacing={3}>
        <Grid item xs={4}>
          <Paper className={classes.control} variant="outlined">
            <ThemeSwitch />
            <Typography
              className={classes.text}
              id="discrete-slider"
              gutterBottom
            >
              Array Length
            </Typography>
            <Slider
              className="slider"
              min={10}
              max={130}
              step={1}
              ValueLabelComponent={ValueLabelComponent}
              value={arrayLength}
              onChange={handelLenChange}
            />

            <br />
            <FormControl id="shuffle_select" className={classes.formControl}>
              <InputLabel id="demo-simple-select-label">
                Shuffle Algorithm
              </InputLabel>
              <Select
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                value={shuffleAlgo}
                onChange={setShuffleAlgorithm}
              >
                <MenuItem value={1}>Fisherâ€“Yates shuffle</MenuItem>
                <MenuItem value={2}>Spotify Shuffle</MenuItem>
              </Select>
            </FormControl>
            <br />
            <br />
            <FormControl id="sort_select" className={classes.formControl}>
              <InputLabel id="demo-simple-select-label">
                Sorting Algorithm
              </InputLabel>
              <Select
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                value={sortAlgo}
                onChange={setSortAlgorithm}
              >
                <MenuItem value={1}>Bubble Sort</MenuItem>
                <MenuItem value={2}>Merge Sort</MenuItem>
                <MenuItem value={3}>Quick Sort</MenuItem>
              </Select>
            </FormControl>
            <br />
            <br />
            <Button
              id="sort_btn"
              className={classes.button}
              variant="contained"
              color="primary"
              onClick={handelStartSorting}
            >
              {isSortReady ? "Reset" : "Start"}
            </Button>
            <Button
              id="shuffle_btn"
              className={classes.button}
              variant="contained"
              color="primary"
              onClick={startShuffle}
            >
              Shuffle
            </Button>
          </Paper>
        </Grid>
      </Grid>
    </>
  );
};

const mapStateToProps = (state) => ({
  isSortReady: state.isSortReady,
  sortingTarget: state.sortTarget,
  sortAlgo: state.sortAlgo,
  shuffleAlgo: state.shuffleAlgo,
  arrayLength: state.arrayLength,
  speed: state.speed,
});

const mapDispatchToProps = (dispatch) => ({
  setArrayLen: (len) => dispatch(setArrayLen(len)),
  setSortAlgorithm: (algo) => dispatch(setSortAlgorithm(algo)),
  setShuffleAlgorithm: (algo) => dispatch(setShuffleAlgorithm(algo)),
  startSorting: (arr) => dispatch(startSorting(arr)),
  startShuffle: (arr) => dispatch(startShuffle(arr)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Control);
