import React, { useState } from "react";
import Container from "@material-ui/core/Container";
import Grid from "@material-ui/core/Grid";
import "./App.css";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import Tooltip from "@material-ui/core/Tooltip";
import Slider from "@material-ui/core/Slider";
import { makeStyles } from "@material-ui/core/styles";
import "bootstrap/dist/css/bootstrap.min.css";
import Visualizer from "./Visualizer/Visualizer";
import Button from "@material-ui/core/Button";
import BubbleSort from "./Visualizer/SortAlgo/BubbleSort";

const ANIMATION_SPEED_MS = 2;
// This is the main color of the array bars.
const PRIMARY_COLOR = "turquoise";

// This is the color of array bars that are being compared throughout the animations.
const SECONDARY_COLOR = "red";

const useStyles = makeStyles({
  control: {
    width: 400,
    padding: "20px",
    textAlign: "center"
  },
  barDiv: {
    display: "flex",
    width: "60%"
  }
});

function ValueLabelComponent(props) {
  const { children, open, value } = props;

  const popperRef = React.useRef(null);
  React.useEffect(() => {
    if (popperRef.current) {
      popperRef.current.update();
    }
  });

  return (
    <Tooltip
      PopperProps={{
        popperRef
      }}
      open={open}
      enterTouchDelay={0}
      placement="top"
      title={value}
    >
      {children}
    </Tooltip>
  );
}

function App() {
  const classes = useStyles();
  const [useLength, SetLength] = useState(10);
  const arrayforSort = [
    191,
    195,
    279,
    275,
    144,
    259,
    278,
    54,
    121,
    183,
    192,
    237,
    299,
    270,
    261,
    102,
    171,
    193,
    85,
    76,
    169,
    173,
    263,
    105,
    127,
    162,
    222,
    211,
    77,
    224,
    266,
    84,
    199,
    151,
    201,
    74,
    254,
    107,
    200,
    181,
    103,
    139,
    120,
    286,
    148,
    133,
    58,
    150,
    91,
    253
  ];
  var animation;
  const onLenSliderChange = (e, value) => {
    SetLength(value);
  };

  const onSortButtonClick = e => {
    const animations = BubbleSort(arrayforSort);
    for (let i = 0; i < animations.length; i++) {
      const arrayBars = document.getElementsByClassName("makeStyles-bar-236");
      const isColorChange = i % 4 === 0 || i % 4 === 1;
      if (isColorChange) {
        const [barOneIdx, barTwoIdx] = animations[i];
        const barOneStyle = arrayBars[barOneIdx].style;
        const barTwoStyle = arrayBars[barTwoIdx].style;
        const color = i % 4 === 0 ? SECONDARY_COLOR : PRIMARY_COLOR;
        setTimeout(() => {
          barOneStyle.backgroundColor = color;
          barTwoStyle.backgroundColor = color;
        }, i * ANIMATION_SPEED_MS);
      } else {
        setTimeout(() => {
          const [barOneIdx, barTwoIdx] = animations[i];
          const barOneStyle = arrayBars[barOneIdx].style;
          const barTwoStyle = arrayBars[barTwoIdx].style;
          const tempH = barOneStyle.height;
          barOneStyle.height = `${barTwoStyle.height}px`;
          barTwoStyle.height = `${tempH}px`;
        }, i * ANIMATION_SPEED_MS);
      }
    }
  };

  return (
    <div className="App">
      <header className="App-header"></header>
      <Container>
        <Grid container spacing={30}>
          <Grid item spacing={3}>
            <div>Slider</div>
            <Paper className={classes.control}>
              <Typography gutterBottom>Array Range</Typography>
              <Slider
                className="slider"
                min={10}
                max={50}
                step={1}
                ValueLabelComponent={ValueLabelComponent}
                value={useLength}
                onChange={onLenSliderChange}
              />
              <p>
                <span>{"The value of this slider is: "}</span>
                <span>{useLength}</span>
                <span>{" from a range of 0 to 200 inclusive"}</span>
              </p>
              <Button
                variant="contained"
                color="primary"
                onClick={onSortButtonClick}
              >
                Sort
              </Button>
            </Paper>
            <Grid item spacing={2}></Grid>
          </Grid>
          <Grid item spacing={7}>
            <div className={classes.barDiv}>
              <Visualizer
                numBar={useLength}
                arr={arrayforSort.slice(0, useLength)}
                animation={animation}
              />
            </div>
          </Grid>
        </Grid>
      </Container>
    </div>
  );
}

export default App;
