import React, { Component } from "react";
import PropTypes from "prop-types";
import { withStyles, makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Slider from "@material-ui/core/Slider";
import Tooltip from "@material-ui/core/Tooltip";

function ValueLabelComponent(props) {
  const { children, open, value } = props;

  const popperRef = React.useRef(null);
  React.useEffect(() => {
    if (popperRef.current) {
      popperRef.current.update();
    }
  });

  return (
    <Tooltip
      PopperProps={{
        popperRef
      }}
      open={open}
      enterTouchDelay={0}
      placement="top"
      title={value}
    >
      {children}
    </Tooltip>
  );
}

export default class CustomizedSlider extends Component {
  state = {
    SliderVal: 0
  };

  handleSlide = (event, value) => {
    this.setState({ SliderVal: value });
  };

  render() {
    return (
      <div style={rootStyle}>
        <Slider
          className="slider"
          min={0}
          max={200}
          step={1}
          ValueLabelComponent={ValueLabelComponent}
          defaultValue={100}
          onChange={val => this.handleSlide(val)}
        />
        <p>
          <span>{"The value of this slider is: "}</span>
          <span>{this.state.SliderVal}</span>
          <span>{" from a range of 0 to 200 inclusive"}</span>
        </p>
      </div>
    );
  }
}

const rootStyle = {
  width: 300,
  padding: "20px"
};
