export const getSortingGenerator = (visualizer, algo) => {
  // 1. bubble sort
  if (algo === 1) {
    return visualizer.BubbleSort();
  }
  //2. merge sort

  //3. quick sort

  //4.
};

export const getSortTarget = (size, arr) => {
  let i = 0;
  let sortObj = [];
  for (; i < size; i++) {
    sortObj.push({
      value: arr[i],
      isCompared: false,
      isSwapped: false,
      isDone: false,
    });
  }
  return sortObj;
};
