import React from "react";
import { connect } from "react-redux";
import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import Tooltip from "@material-ui/core/Tooltip";
import Slider from "@material-ui/core/Slider";
import FormControl from "@material-ui/core/FormControl";
import MenuItem from "@material-ui/core/MenuItem";
import Select from "@material-ui/core/Select";
import InputLabel from "@material-ui/core/InputLabel";
import Button from "@material-ui/core/Button";
import ThemeSwitch from "./ThemeSwitch";
import FormLabel from "@material-ui/core/FormLabel";
import Radio from "@material-ui/core/Radio";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import RadioGroup from "@material-ui/core/RadioGroup";
import {
  setArrayLen,
  setShuffleAlgorithm,
  setSortAlgorithm,
  startShuffle,
  startSorting,
  setSpeed,
} from "../actions";
const useStyles = makeStyles((theme) => ({
  control: {
    width: "25%",
    height: "50%",
    padding: "10px",
    textAlign: "left",
    alignItems: "center",
    margin: "10px",
  },
  slider: { width: "80%", padding: "10px", margin: "10px" },
  barDiv: {
    display: "flex",
    width: "60%",
  },
  button: {
    padding: "10px",
    margin: "10px",
  },
  formControl: {
    alignItems: "center",
    width: "90%",
  },
  select: { width: "90%" },
  root: { marginTop: "30px" },
  heading: {
    fontSize: theme.typography.pxToRem(15),
    flexBasis: "33.33%",
    flexShrink: 0,
  },
  secondaryHeading: {
    fontSize: theme.typography.pxToRem(15),
    color: theme.palette.text.secondary,
  },
}));
function ValueLabelComponent(props) {
  const { children, open, value } = props;

  const popperRef = React.useRef(null);
  React.useEffect(() => {
    if (popperRef.current) {
      popperRef.current.update();
    }
  });

  return (
    <Tooltip
      PopperProps={{
        popperRef,
      }}
      open={open}
      enterTouchDelay={0}
      placement="top"
      title={value}
    >
      {children}
    </Tooltip>
  );
}

const Control = ({
  isSortReady,
  arrayLength,
  setArrayLen,
  sortAlgo,
  shuffleAlgo,
  startSorting,
  startShuffle,
  setSpeed,
  speed,
  setSortAlgorithm,
}) => {
  const classes = useStyles();

  const handelLenChange = (e, value) => {
    setArrayLen(value);
  };
  const handleSpeedChange = (e, value) => {
    setSpeed(value);
  };
  const handleSortAlgoChange = (e, target) => {
    setSortAlgorithm(target.value);
  };
  const handelStartSorting = (e) => {
    startSorting(!isSortReady);
  };

  return (
    <>
      <Paper className={classes.control} variant="outlined">
        <Typography className={classes.text} id="discrete-slider" gutterBottom>
          Array Length
        </Typography>
        <Slider
          className={classes.slider}
          min={10}
          max={130}
          step={1}
          ValueLabelComponent={ValueLabelComponent}
          value={arrayLength}
          onChange={handelLenChange}
        />

        <br />
        <FormControl id="shuffle_select" className={classes.formControl}>
          <InputLabel id="demo-simple-select-label">
            Shuffle Algorithm
          </InputLabel>
          <Select
            className={classes.select}
            labelId="demo-simple-select-label"
            id="demo-simple-select"
            value={shuffleAlgo}
            onChange={setShuffleAlgorithm}
          >
            <MenuItem value={1}>Fisherâ€“Yates shuffle</MenuItem>
            <MenuItem value={2}>Spotify Shuffle</MenuItem>
          </Select>
        </FormControl>
        <br />
        <br />
        <FormControl id="sort_select" className={classes.formControl}>
          <InputLabel id="demo-simple-select-label">
            Sorting Algorithm
          </InputLabel>
          <Select
            className={classes.select}
            labelId="demo-simple-select-label"
            id="demo-simple-select"
            value={sortAlgo}
            onChange={handleSortAlgoChange}
          >
            <MenuItem value={1}>Bubble Sort</MenuItem>
            <MenuItem value={2}>Merge Sort</MenuItem>
            <MenuItem value={3}>Quick Sort</MenuItem>
            <MenuItem value={4}>Insertion Sort</MenuItem>
            <MenuItem value={5}>Selection Sort</MenuItem>
            <MenuItem value={6}>Heap Sort</MenuItem>
          </Select>
        </FormControl>
        <br />
        <br />
        <Typography>
          Dark Mode <ThemeSwitch />
        </Typography>

        <FormLabel className={classes.radioGroup} component="legend">
          Animation Speed
        </FormLabel>
        <RadioGroup
          aria-label="speed"
          name="speed"
          value={speed}
          onChange={handleSpeedChange}
          row
        >
          <FormControlLabel
            value="50"
            control={<Radio color="primary" />}
            label="slow"
          />
          <FormControlLabel
            value="100"
            control={<Radio color="primary" />}
            label="mid"
          />
          <FormControlLabel
            value="200"
            control={<Radio color="primary" />}
            label="fast"
          />
        </RadioGroup>
        <Button
          id="sort_btn"
          className={classes.button}
          variant="contained"
          color="primary"
          onClick={handelStartSorting}
        >
          {isSortReady ? "Reset" : "Start"}
        </Button>
        <Button
          id="shuffle_btn"
          className={classes.button}
          variant="contained"
          color="primary"
          onClick={startShuffle}
        >
          Shuffle
        </Button>
      </Paper>
    </>
  );
};

const mapStateToProps = (state) => ({
  isSortReady: state.isSortReady,
  sortingTarget: state.sortTarget,
  sortAlgo: state.sortAlgo,
  shuffleAlgo: state.shuffleAlgo,
  arrayLength: state.arrayLength,
  speed: state.speed,
});

const mapDispatchToProps = (dispatch) => ({
  setArrayLen: (len) => dispatch(setArrayLen(len)),
  setSortAlgorithm: (algo) => dispatch(setSortAlgorithm(algo)),
  setShuffleAlgorithm: (algo) => dispatch(setShuffleAlgorithm(algo)),
  startSorting: (arr) => dispatch(startSorting(arr)),
  startShuffle: (arr) => dispatch(startShuffle(arr)),
  setSpeed: (speed) => dispatch(setSpeed(speed)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Control);
