import swap from "./swap";

function Merge(array, temp, left, mid, right, animation) {
  var k = left;
  var i = left;
  var j = mid + 1;

  while (i <= mid && j <= right) {
    animation.push([i, k]);
    animation.push([i, k]);
    if (array[i] >= array[j]) {
      animation.push([k, array[i]]);
      animation.push([k, array[i]]);
      //temp[k++] = array[i++];
      array[k++] = temp[i++];
      //   swap(array, k++, i++);
      console.log(i, k);
      //   i++;
      //   k++;
    } else {
      animation.push([k, array[j]]);
      animation.push([k, array[j]]);
      //   temp[k++] = array[j++];
      array[j++] = temp[k++];
      //   swap(array, k++, j++);
      console.log(j, k);
      //   j++;
      //   k++;
    }
  }
  while (i <= mid) {
    animation.push([i, array[i]]);
    animation.push([i, array[i]]);
    animation.push([k, array[i]]);
    animation.push([k, array[i]]);
    //temp[k++] = array[i++];
    array[k++] = temp[i++];
  }
  while (j <= right) {
    animation.push([j, array[j]]);
    animation.push([j, array[j]]);
    animation.push([k, array[j]]);
    animation.push([k, array[j]]);
    //temp[k++] = array[j++];
    array[k++] = temp[j++];
  }
}

function MergeSort(array) {
  var left = 0;
  var right = array.length - 1;
  //var mid = (right+1)/2;
  var animation = [];
  const temp = array.slice(); //make a copy of array
  for (let i = 1; i <= right - left; i = i * 2) {
    for (let j = left; j < right; j += 2 * i) {
      var from = j;
      var mid = j + i - 1;
      var to = Math.min(j + 2 * i - 1, right);
      Merge(array, temp, from, mid, to, animation);
    }
  }

  return animation;
}

export default MergeSort;
