import React, { Component } from "react";
import CustomizedSlider from "./CustomizedSlider";
import Paper from "@material-ui/core/Paper";
import { withStyles, makeStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import { textAlign } from "@material-ui/system";

export default class ControlCenter extends Component {
  state = {
    Slider_1: 100
  };
  handleSlide_1 = (event, value) => {
    this.setState({ Slider_1: value });
  };
  render() {
    return (
      <Paper style={controlStyle}>
        <Typography gutterBottom>Array Range</Typography>
        <CustomizedSlider className="Slider_1" />
        <p>
          <span>{"The value of this slider is: "}</span>
          <span>{this.props.slider}</span>
          <span>{" from a range of 0 to 200 inclusive"}</span>
        </p>
      </Paper>
    );
  }
}

const controlStyle = {
  width: 400,
  padding: "20px",
  textAlign: "center"
};
