import swap from "./swap";

function BubbleSort(array) {
  var animation = [];
  var len = array.length;
  var i, j;
  for (i = 0; i < len - 1; i++) {
    for (j = 0; j < len - i - 1; j++) {
      if (array[j] < array[j + 1]) {
        swap(array, j, j + 1);
        animation.push([j, j + 1]);
      }
    }
  }

  return animation;
}
export default BubbleSort;
