import getrandomIntArray from "../randomIntArray";
import {
  UPDATEARRAY,
  UPDATESHUFFLEALGO,
  UPDATEARRAYLEN,
  UPDATESORTALGO,
  ISSORTING,
  STARTSORTING,
  ISDARKMODE,
  NEXTACTION,
} from "../constants/actionTypes";
import sortingVisualizer from "../Visualizer/SortingVisualizer";
import { getSortingGenerator, getSortTarget } from "../Utils/utils";
import { ISwapAction, ISortTargetPayload } from "../constants/Itypes";

interface IState {
  isSortReady: boolean;
  isShuffleReady: boolean;
  isSorting: boolean;
  initArray: Array<number>;
  sortTarget: Array<ISortTargetPayload>;
  sortAlgo: number;
  shuffleAlgo: number;
  arrayLength: number;
  actions: IterableIterator<ISwapAction> | null;
  nextactions: ISwapAction | null;
  speed: number;
  isDark: boolean;
}

const initState: IState = {
  isSortReady: false,
  isShuffleReady: false,
  isSorting: false,
  initArray: getrandomIntArray(),
  sortTarget: getSortTarget(10, getrandomIntArray()),
  sortAlgo: 1,
  shuffleAlgo: 1,
  arrayLength: 10,
  actions: null,
  nextactions: null,
  speed: 50,
  isDark: false,
};

export default function paramReducer(
  state: IState = initState,
  action: any
): IState {
  // let actions = null;
  // let nextAction = null;
  switch (action.type) {
    case STARTSORTING:
      const isSortReady = action.payload;
      // console.log(isSortReady);
      let { sortTarget } = state;
      let actions = null;
      let nextAction: ISwapAction | null = null;
      if (isSortReady) {
        const { sortAlgo } = state;
        // console.log(sortAlgo);
        const s = new sortingVisualizer(sortTarget);
        actions = getSortingGenerator(s, sortAlgo);
        // console.log(actions);
      } else {
        sortTarget = getSortTarget(state.arrayLength, state.initArray);
        actions = null;
        nextAction = null;
      }
      return {
        ...state,
        actions,
        isSortReady,
        nextactions: nextAction,
        sortTarget,
      };
    case UPDATEARRAY:
      // console.log("here in update array");
      // const { arr } = ;
      return { ...state, sortTarget: action.payload.sortTarget };
    case UPDATESORTALGO:
      return { ...state, sortAlgo: action.payload };
    case UPDATESHUFFLEALGO:
      return { ...state, shuffleAlgo: action.payload };
    case UPDATEARRAYLEN:
      let newTarget = getSortTarget(action.payload, state.initArray);
      // actions = null;
      // nextAction = null;
      return {
        ...state,
        arrayLength: action.payload,
        sortTarget: newTarget,
      };
    case ISSORTING:
      return { ...state, isSorting: action.payload };
    case ISDARKMODE:
      const { isDark } = action.payload;
      return { ...state, isDark };
    case NEXTACTION:
      //update next action
      // let nextAction = action.payload;
      const ready = state.isSortReady;
      // if (!shouldSort) nextAction = null;
      return {
        ...state,
        nextactions: ready ? action.payload : null,
      };
    default:
      return { ...state };
  }
}
