import React from "react";
import styled from "styled-components";

const mapPropsToBGC = (isCompared, isSwapped) => {
  if (isCompared) {
    return `var(--compared)`;
  }
  if (isSwapped) {
    return `var(--swapped)`;
  }
  return `var(--copyOtherSidebar)`;
};

const mapPropsToTransition = (speed) => {
  const minInterval = Math.min(300, speed);
  return `all ease-in-out ${minInterval}ms`;
};
const Bar = styled.div`
  height: ${(props) => props.height};
  flex: 1;
  background: ${(props) => mapPropsToBGC(props.isCompared, props.isSwapped)};
  transition: ${(props) => mapPropsToTransition(props.speed)};
  color: var(--background);
  justify-content: center;
  align-items: center;
  display: flex;
  border: 1px solid var(--background);
`;

export default function SortingBar({ element, speed }) {
  const height = `${Math.floor((element.value / 700) * 100)}vh`;
  return (
    <Bar
      height={height}
      isSwapped={element.isSwapped}
      isCompared={element.isCompared}
      speed={speed}
    ></Bar>
  );
}
