import {
  UPDATEARRAY,
  UPDATESHUFFLEALGO,
  UPDATEARRAYLEN,
  UPDATESORTALGO,
  ISSORTING,
  SETSWAP,
  SETBARS,
  SETSPEED,
  ISDARKMODE,
  SETACTION,
  STARTSORTING,
  NEXTACTION,
} from "../constants/actionTypes";

export const setSwap = (payload) => ({
  type: SETSWAP,
  payload,
});

export const setSpeed = (payload) => ({
  type: SETSPEED,
  payload,
});

export const setSortAlgorithm = (payload) => ({
  type: UPDATESORTALGO,
  payload,
});

export const setShuffleAlgorithm = (payload) => ({
  type: UPDATESHUFFLEALGO,
  payload,
});

export const updateArray = (payload) => ({
  type: UPDATEARRAY,
  payload,
});

export const setArrayLen = (payload) => ({
  type: UPDATEARRAYLEN,
  payload,
});

export const setActions = (payload) => ({
  type: SETACTION,
  payload,
});

export const isSorting = (payload) => ({
  type: ISSORTING,
  payload,
});

export const setTheme = (payload) => ({
  type: ISDARKMODE,
  payload,
});

export const sleep = (ms) => {
  new Promise((r) => setTimeout(r, ms));
};

export const startSorting = (payload) => async (dispatch, getState) => {
  dispatch({ type: STARTSORTING, payload });
  const { actions } = getState();
  console.log(actions);
  if (actions !== null) {
    let { isSortReady, speed } = getState();
    while (isSortReady) {
      let newAction = actions.next();
      const payload = newAction.value;
      if (payload === undefined) {
        const cleanAction = {
          type: "clean",
        };
        dispatch({
          type: NEXTACTION,
          payload: cleanAction,
        });
        break;
      }
      if (payload.type === "comparison") {
        const cleanAction = {
          type: "clean",
        };
        dispatch({
          type: NEXTACTION,
          payload: cleanAction,
        });
        await sleep(speed);
      } else if (payload.type === "swap") {
        const swapColors = {
          type: "swapColors",
          first: payload.first,
          second: payload.second,
        };
        dispatch({
          type: NEXTACTION,
          payload: swapColors,
        });
        await sleep(speed);
      }
      dispatch({
        type: NEXTACTION,
        payload,
      });
      await sleep(speed);
      let newState = getState();
      isSortReady = newState.isSortReady;
      newAction = newState.nextAction;
      speed = newState.speed;
    }
  }
};

export const startShuffle = (payload) => {};
