import swap from "../SortAlgo/swap";

function FYShuffle(array) {
  const len = array.length;
  var i = len - 1;
  var animation = [];
  for (; i > 0; i--) {
    var random = Math.floor(Math.random() * (+i - +0)) + +0;
    animation.push([i, random]);
    animation.push([i, random]);
    animation.push([i, array[i]]);
    animation.push([random, array[random]]);
    swap(array, i, random);
  }
  return animation;
}

export default FYShuffle;
