import React, { useState } from "react";
import ControlCenter from "./components/ControlCenter";
//import SortingBar from "./components";
import "./App.css";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import Tooltip from "@material-ui/core/Tooltip";
import Slider from "@material-ui/core/Slider";
import { makeStyles } from "@material-ui/core/styles";
import "bootstrap/dist/css/bootstrap.min.css";
import Visualizer from "./Visualizer/Visualizer";

const useStyles = makeStyles({
  control: {
    width: 400,
    padding: "20px",
    textAlign: "center"
  }
});

function ValueLabelComponent(props) {
  const { children, open, value } = props;

  const popperRef = React.useRef(null);
  React.useEffect(() => {
    if (popperRef.current) {
      popperRef.current.update();
    }
  });

  return (
    <Tooltip
      PopperProps={{
        popperRef
      }}
      open={open}
      enterTouchDelay={0}
      placement="top"
      title={value}
    >
      {children}
    </Tooltip>
  );
}

function App() {
  const classes = useStyles();
  const [useLength, SetLength] = useState(10);

  const onLenSliderChange = () => (e, value) => {
    SetLength(value);
  };

  return (
    <div className="App">
      <header className="App-header"></header>
      <div>Slider</div>
      <Paper className={classes.control}>
        <Typography gutterBottom>Array Range</Typography>
        <Slider
          className="slider"
          min={0}
          max={200}
          step={1}
          ValueLabelComponent={ValueLabelComponent}
          value={useLength}
          onChange={onLenSliderChange}
        />
        <p>
          <span>{"The value of this slider is: "}</span>
          <span>{useLength}</span>
          <span>{" from a range of 0 to 200 inclusive"}</span>
        </p>
      </Paper>
      <div>
        <Visualizer numBar={4} arr={[10, 20, 30, 40, 50]} />
      </div>
    </div>
  );
}

export default App;
