import getrandomIntArray from "../randomIntArray";
import {
  UPDATEARRAY,
  UPDATESHUFFLEALGO,
  UPDATEARRAYLEN,
  UPDATESORTALGO,
  ISSORTING,
  STARTSORTING,
  ISDARKMODE,
  NEXTACTION,
  PERFORMNEXTACTION,
} from "../constants/actionTypes";
import { sortingVisualizer } from "../Visualizer/SortingVisualizer";
import { getSortingGenerator, getSortTarget } from "../Utils/utils";

const initState = {
  isSortReady: false,
  isShuffleReady: false,
  isSorting: false,
  initArray: getrandomIntArray(),
  sortTarget: getSortTarget(10, getrandomIntArray()),
  sortAlgo: 1,
  shuffleAlgo: 1,
  arrayLength: 10,
  actions: null,
  nextactions: null,
  speed: 20,
  isDark: false,
};

export default function paramReducer(state = initState, action) {
  // let actions = null;
  // let nextAction = null;
  switch (action.type) {
    case STARTSORTING:
      const isSortReady = action.payload;
      // console.log(isSortReady);
      let { sortTarget } = state;
      let actions = null;
      let nextAction = null;
      if (isSortReady) {
        const { sortAlgo } = state;
        // console.log(sortAlgo);
        const s = new sortingVisualizer(sortTarget);
        actions = getSortingGenerator(s, sortAlgo);
        // console.log(actions);
      } else {
        sortTarget = getSortTarget(state.arrayLength, state.initArray);
        actions = null;
        nextAction = null;
      }
      return { ...state, actions, isSortReady, nextAction, sortTarget };
    case UPDATEARRAY:
      // console.log("here in update array");
      // const { arr } = ;
      return { ...state, sortTarget: action.payload.sortTarget };
    case UPDATESORTALGO:
      return { ...state, sortAlgo: action.payload };
    case UPDATESHUFFLEALGO:
      return { ...state, shuffleAlgo: action.payload };
    case UPDATEARRAYLEN:
      let newTarget = getSortTarget(action.payload, state.initArray);
      // actions = null;
      // nextAction = null;
      return { ...state, arrayLength: action.payload, sortTarget: newTarget };
    case ISSORTING:
      return { ...state, isSorting: action.payload };
    case ISDARKMODE:
      return { ...state, isDark: action.payload };
    case NEXTACTION:
      // let nextAction = action.payload;
      const ready = state.isSortReady;
      // if (!shouldSort) nextAction = null;
      return { ...state, nextactions: ready ? action.payload : null };
    case PERFORMNEXTACTION:
      // let nextAction = action.payload;
      if (state.isSortReady) {
        let newArray = [...state.sortTarget];
        if (action.payload.type === "comparison") {
          newArray[nextAction.first].isCompared = true;
          newArray[nextAction.second].isCompared = true;
          // updateArray(newArray);
        } else if (action.payload.type === "swapColors") {
          let newArray = [...sortTarget];
          newArray[nextAction.first].isCompared = false;
          newArray[nextAction.second].isCompared = false;
          newArray[nextAction.first].isSwapped = true;
          newArray[nextAction.second].isSwapped = true;
          // updateArray(newArray);
        } else if (action.payload.type === "swap") {
          let newArray = [...sortTarget];
          let temp = newArray[nextAction.first];
          newArray[nextAction.first] = newArray[nextAction.second];
          newArray[nextAction.second] = temp;
          // setBars(barArray);
          // updateArray(newArray);
        } else if (action.payload.type === "clean") {
          let newArray = [...sortTarget];
          newArray.forEach((b) => {
            b.isCompared = false;
            b.isSwapped = false;
          });
          // updateArray(newArray);
        }
      }
      // if (!shouldSort) nextAction = null;
      return { ...state, nextactions: ready ? action.payload : null };
    default:
      return { ...state };
  }
}
