import swap from "./swap";

function Merge(array, temp, left, mid, right, animation) {
  var k = left;
  var i = left;
  var j = mid + 1;

  while (i <= mid && j <= right) {
    if (array[i] > array[j]) {
      animation.push([i, j]);
      animation.push([i, j]);
      animation.push([i, array[j]]);
      animation.push([j, array[i]]);
      temp[k++] = array[i++];
      console.log(i, k);
      //   i++;
      //   k++;
    } else {
      //   animation.push([j, k]);
      //   animation.push([j, k]);
      //   animation.push([j, array[k]]);
      //   animation.push([k, array[j]]);
      temp[k++] = array[j++];
      console.log(j, k);
      //   j++;
      //   k++;
    }
  }
  while (i <= mid) {
    temp[k++] = array[i++];
  }
  for (i = left; i <= right; i++) {
    array[i] = temp[i];
  }
}

function MergeSort(array) {
  var left = 0;
  var right = array.length - 1;
  //var mid = (right+1)/2;
  var animation = [];
  const temp = array.slice(); //make a copy of array
  for (let i = 1; i <= right - left; i = i * 2) {
    for (let j = left; j < right; j += 2 * i) {
      var from = j;
      var mid = j + i - 1;
      var to = Math.min(j + 2 * i - 1, right);
      Merge(array, temp, from, mid, to, animation);
    }
  }

  return animation;
}

export default MergeSort;
