import React, { Component } from "react";
import CustomizedSlider from "./CustomizedSlider";
import Paper from "@material-ui/core/Paper";
import { withStyles, makeStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import { textAlign } from "@material-ui/system";
import Tooltip from "@material-ui/core/Tooltip";
import Slider from "@material-ui/core/Slider";

function ValueLabelComponent(props) {
  const { children, open, value } = props;

  const popperRef = React.useRef(null);
  React.useEffect(() => {
    if (popperRef.current) {
      popperRef.current.update();
    }
  });

  return (
    <Tooltip
      PopperProps={{
        popperRef
      }}
      open={open}
      enterTouchDelay={0}
      placement="top"
      title={value}
    >
      {children}
    </Tooltip>
  );
}

export default class ControlCenter extends Component {
  constructor(props) {
    super(props);
    this.state = {
      slider_1: 100
    };
  }
  onSliderChange = name => (e, value) => {
    //console.log(value);
    this.setState({
      [name]: value
    });
  };

  render() {
    return (
      <Paper style={controlStyle}>
        <Typography gutterBottom>Array Range</Typography>
        <Slider
          className="slider"
          min={0}
          max={200}
          step={1}
          ValueLabelComponent={ValueLabelComponent}
          value={this.state.slider_1}
          onChange={this.onSliderChange("slider_1")}
        />
        <p>
          <span>{"The value of this slider is: "}</span>
          <span>{this.props.value}</span>
          <span>{" from a range of 0 to 200 inclusive"}</span>
        </p>
      </Paper>
    );
  }
}

const controlStyle = {
  width: 400,
  padding: "20px",
  textAlign: "center"
};
